function GetImagesEvent(){const e=document.querySelectorAll("li.list-group-item");for(let t=0;t<e.length;t++)e[t].addEventListener("click",function(){changeImage(this)})}function changeImage(e){document.querySelector("li.selected").classList.remove("selected"),e.classList.add("selected"),document.getElementById("image-displayed").src=e.querySelector("img").src}function searchImages(){document.getElementById("search-box").addEventListener("keyup",function(){if(this.value.length>0){const e=new RegExp(this.value.toLowerCase(),"gi");if(this.value.length>0){const t=document.querySelectorAll("li.list-group-item img");for(let l=0;l<t.length;l++){const s=url.pathToFileURL(t[l].src),n=path.basename(s.pathname);console.log(n),n.match(e),t[l].parentNode.classList.remove("hidden")}selectFirstImage()}}else showAll()})}function selectFirstImage(){changeImage(document.querySelector("li.list-group-item:not(.hidden)"))}function showAll(){const e=document.querySelectorAll(".li.list-group-item");for(let t=0;t<e.length;t++)listItem[t].classList.remove("hidden");selectFirstImage()}import url from"url";import path from"path";window.addEventListener("load",()=>{GetImagesEvent(),searchImages()});