function GetImagesEvent(){const e=document.querySelectorAll("li.list-group-item");for(let t=0;t<e.length;t++)e[t].addEventListener("click",function(){changeImage(this)})}function changeImage(e){document.querySelector("li.selected").classList.remove("selected"),e.classList.add("selected"),document.getElementById("image-displayed").src=e.querySelector("img").src}function searchImages(){document.getElementById("search-box").addEventListener("keyup",function(){const e=new RegExp(this.value.toLowerCase(),"gi");if(this.value.length>0){const t=document.querySelectorAll("li.list-group-item img");for(let n=0;n<t.length;n++){const o=url.pathToFileURL(t[n].src),s=path.basename(o.pathname);console.log(s),s.match(e),t[n].parentNode.classList.remove("hidden")}}})}import url from"url";import path from"path";window.addEventListener("load",()=>{GetImagesEvent(),searchImages()});