function OpenDirectory(){document.getElementById("open-directory").addEventListener("click",()=>{sendIpc()})}function GetImagesEvent(){const e=document.querySelectorAll("li.list-group-item");for(let t=0;t<e.length;t++)e[t].addEventListener("click",function(){changeImage(this)})}function selectFilter(){document.getElementById("filters").addEventListener("change",function(){applyFilter(this.value,document.getElementById("image-displayed"))})}function changeImage(e){e?(document.querySelector("li.selected").classList.remove("selected"),e.classList.add("selected"),document.getElementById("image-displayed").src=e.querySelector("img").src):document.getElementById("image-displayed").src=""}function searchImages(){document.getElementById("search-box").addEventListener("keyup",function(){const e=new RegExp(this.value.toLowerCase(),"gi");if(this.value.length>0){const t=document.querySelectorAll("li.list-group-item img");for(let n=0;n<t.length;n++){const i=url.pathToFileURL(t[n].src);path.basename(i.pathname).match(e)?t[n].parentNode.classList.remove("hidden"):t[n].parentNode.classList.add("hidden")}selectFirstImage()}else showAll(),selectFirstImage()})}function selectFirstImage(){changeImage(document.querySelector("li.list-group-item:not(.hidden)"))}function showAll(){const e=document.querySelectorAll("li.list-group-item img");for(let t=0;t<e.length;t++)e[t].parentNode.classList.remove("hidden")}import url from"url";import path from"path";import applyFilter from"./filters";import{setIpc,sendIpc}from"./ipcRendererEvents";window.addEventListener("load",()=>{setIpc(),GetImagesEvent(),searchImages(),selectFilter(),OpenDirectory()});