function changeImage(e){if(e){const t=document.querySelector("li.selected");t&&t.classList.remove("selected"),e.classList.add("selected");const l=document.getElementById("image-displayed");l.src=e.querySelector("img").src,l.dataset.original=l.src,document.getElementById("filters").selectedIndex=0}else document.getElementById("image-displayed").src=""}function GetImagesEvent(){const e=document.querySelectorAll("li.list-group-item");for(let t=0;t<e.length;t++)e[t].addEventListener("click",function(){changeImage(this)})}function selectFirstImage(){changeImage(document.querySelector("li.list-group-item:not(.hidden)"))}function selectFilter(){document.getElementById("filters").addEventListener("change",function(){applyFilter(this.value,document.getElementById("image-displayed"))})}function searchImages(){document.getElementById("search-box").addEventListener("keyup",function(){const e=new RegExp(this.value.toLowerCase(),"gi");if(this.value.length>0){const t=document.querySelectorAll("li.list-group-item img");for(let l=0;l<t.length;l++){const s=url.pathToFileURL(t[l].src);path.basename(s.pathname).match(e)?t[l].parentNode.classList.remove("hidden"):t[l].parentNode.classList.add("hidden")}selectFirstImage()}else showAll(),selectFirstImage()})}function showAll(){const e=document.querySelectorAll("li.list-group-item img");for(let t=0;t<e.length;t++)e[t].parentNode.classList.remove("hidden")}function DeleteImages(){const e=document.querySelectorAll("li.list-group-item");for(let t=0;t<e.length;t++)e[t].parentNode.removeChild(e[t])}function LoadImages(e){const t=document.querySelector("ul.list-group");for(let l=0;l<e.length;l++){const s=`<li class="list-group-item">\n                    <img class="img-circle media-object pull-left" src="${e[l].src}" width="32" height="32">\n                        <div class="media-body">\n                            <strong>${e[l].fileName}</strong>\n                                <p>${e[l].size}</p>\n                        </div>\n                    </li>`;t.insertAdjacentHTML("beforeend",s)}}import url from"url";import path from"path";import{applyFilter}from"./filters";module.exports={changeImage:changeImage,GetImagesEvent:GetImagesEvent,selectFirstImage:selectFirstImage,selectFilter:selectFilter,searchImages:searchImages,DeleteImages:DeleteImages,LoadImages:LoadImages};