function setIpc(){ipcRenderer.on("load-images",(e,o)=>{DeleteImages(),LoadImages(o),GetImagesEvent(),selectFirstImage()}),ipcRenderer.on("save-image",(e,o)=>{console.log(o),saveImage(o,e=>e?showDialog("error","PictureApp",e.message):showDialog("info","PictureApp","Image success saved"))})}function openDirectory(){ipcRenderer.send("open-directory")}function showDialog(e,o,t){ipcRenderer.send("show-dialog",{type:e,title:o,message:t})}function SaveFile(){const e=document.getElementById("image-displayed").dataset.original,o=path.extname(e);ipcRenderer.send("open-save-dialog",o)}import{ipcRenderer}from"electron";import{GetImagesEvent,DeleteImages,LoadImages,selectFirstImage}from"./imagesUi";import path from"path";import{saveImage}from"./filters";module.exports={setIpc:setIpc,SaveFile:SaveFile,openDirectory:openDirectory};